create table racial_modifier
(
    _id INTEGER PRIMARY KEY ASC AUTOINCREMENT,
    race TEXT NOT NULL,
    hpgain INTEGER,
    hpregen INTEGER,
    mngain INTEGER,
    mnregen INTEGER,
    mvgain INTEGER,
    mvregen INTEGER,
    expcost INTEGER,
    tnl INTEGER,
    hitprob INTEGER,
    dammod INTEGER,
    castcost INTEGER,
    castprob INTEGER,
    saveroll INTEGER,
    arc INTEGER,
    stealth INTEGER,
    arcane INTEGER,
    divine INTEGER,
    psionic INTEGER,
    unique(race)
);    
    

